import React, { useState } from 'react';
import Navbar from '../components/Navbar'; 
import axios from 'axios';

function HomePage() {
  const [searchCriteria, setSearchCriteria] = useState({
    originCity: '',
    originState: '',
    originZip: '',
    destinationCity: '',
    destinationState: '',
    destinationZip: '',
  });
  const [rides, setRides] = useState([]);
  const [rideDetails, setRideDetails] = useState({
    originAddress: '',
    originCity: '',
    originZip: '',
    destinationAddress: '',
    destinationCity: '',
    destinationZip: '',
    vehicleId: '',
    availableSeats: '',
    returnBack: false,
  });

  const handleSearch = () => {
    axios
      .get('http://localhost:8080/api/v1/session', { params: searchCriteria })
      .then((response) => setRides(response.data))
      .catch((error) => console.error('Error searching rides:', error));
  };

  const handleStartRide = () => {
    axios
      .post('http://localhost:8080/api/v1/session?expireAt=ONE_HOUR', rideDetails)
      .then(() => alert('Ride started successfully'))
      .catch((error) => console.error('Error starting ride:', error));
  };

  return (
    <div>
      <Navbar />
      <h1>Welcome to CarHop</h1>

      {/* Search for a Ride */}
      <div>
        <h2>Search for a Ride</h2>
        <input
          type="text"
          placeholder="Origin City"
          value={searchCriteria.originCity}
          onChange={(e) => setSearchCriteria({ ...searchCriteria, originCity: e.target.value })}
        />
        <input
          type="text"
          placeholder="Destination City"
          value={searchCriteria.destinationCity}
          onChange={(e) => setSearchCriteria({ ...searchCriteria, destinationCity: e.target.value })}
        />
        <button onClick={handleSearch}>Search</button>
        <div>
          {rides.length > 0 ? (
            rides.map((ride) => (
              <div key={ride.id}>
                <h4>{ride.originCity} â†’ {ride.destinationCity}</h4>
                <p>Seats: {ride.availableSeats}</p>
              </div>
            ))
          ) : (
            <p>No rides found</p>
          )}
        </div>
      </div>

      {/* Start a Ride */}
      <div>
        <h2>Start a Ride</h2>
        <input
          type="text"
          placeholder="Origin Address"
          value={rideDetails.originAddress}
          onChange={(e) => setRideDetails({ ...rideDetails, originAddress: e.target.value })}
        />
        <button onClick={handleStartRide}>Start Ride</button>
      </div>
    </div>
  );
}

export default HomePage;
