import React, { useState } from 'react';
import '../styles/SearchForm.css';
import { searchRides } from '../services/rideService';
import { requestToJoinRide } from '../services/rideService';

const SearchForm = ({ onSearchResults }) => {
  const [formData, setFormData] = useState({
    originCity: '',
    originState: '',
    originZip: '',
    destinationCity: '',
    destinationState: '',
    destinationZip: '',
  });

  const loggedInUserId = localStorage.getItem('userId'); // Logged-in user ID

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSearch = (e) => {
    e.preventDefault();
    const filteredParams = Object.fromEntries(
      Object.entries(formData).filter(([_, value]) => value.trim() !== '')
    );
    searchRides(filteredParams)
      .then((response) => onSearchResults(response.data))
      .catch((error) => console.error('Error searching rides:', error));
  };

  const handleRequest = (rideId) => {
    requestToJoinRide(loggedInUserId, rideId)
      .then(() => alert('Request sent successfully!'))
      .catch((error) => console.error('Error requesting ride:', error));
  };

  return (
    <form className="search-form" onSubmit={handleSearch}>
      {/* Search Form Inputs */}
      <input
        type="text"
        name="originCity"
        placeholder="Origin City"
        value={formData.originCity}
        onChange={handleChange}
      />
      <input
        type="text"
        name="destinationCity"
        placeholder="Destination City"
        value={formData.destinationCity}
        onChange={handleChange}
      />
      <button type="submit" className="btn btn-primary">Search</button>
    </form>
  );
};

const SearchResults = ({ results, loggedInUserId, handleRequest }) => (
  <div className="ride-results">
    {results.length > 0 ? (
      results.map((ride) => (
        <div key={ride.id} className="ride-card">
          <p><strong>Origin:</strong> {ride.originCity}</p>
          <p><strong>Destination:</strong> {ride.destinationCity}</p>
          <p><strong>Available Seats:</strong> {ride.availableSeats}</p>
          {ride.userId === loggedInUserId ? (
            <p className="your-ride-label">Your Ride</p>
          ) : (
            <button
              className="btn btn-success"
              onClick={() => handleRequest(ride.id)}
              disabled={ride.availableSeats <= 0}
            >
              Request to Join
            </button>
          )}
        </div>
      ))
    ) : (
      <p>No rides found.</p>
    )}
  </div>
);


export { SearchForm, SearchResults };
