import React, { useState, useEffect } from 'react';
import { getRideRequests, approveRequest, rejectRequest } from '../services/rideService';
import './TripsPage.css';

const TripsPage = ({ sessionId }) => {
  const [requests, setRequests] = useState([]);

  useEffect(() => {
    // Fetch all requests for the logged-in user's rides
    getRideRequests(sessionId)
      .then((response) => setRequests(response.data))
      .catch((error) => console.error('Error fetching requests:', error));
  }, [sessionId]);

  const handleApprove = (requestId) => {
    approveRequest(requestId)
      .then(() => setRequests((prev) => prev.filter((req) => req.id !== requestId)))
      .catch((error) => console.error('Error approving request:', error));
  };

  const handleReject = (requestId) => {
    rejectRequest(requestId)
      .then(() => setRequests((prev) => prev.filter((req) => req.id !== requestId)))
      .catch((error) => console.error('Error rejecting request:', error));
  };

  return (
    <div className="trips-page">
      <h2>Manage Ride Requests</h2>
      {requests.length > 0 ? (
        <ul className="request-list">
          {requests.map((req) => (
            <li key={req.id} className="request-item">
              <p>
                {req.hopUser.firstName} {req.hopUser.lastName}
              </p>
              <div className="request-actions">
                <button className="btn btn-success" onClick={() => handleApprove(req.id)}>
                  Approve
                </button>
                <button className="btn btn-danger" onClick={() => handleReject(req.id)}>
                  Reject
                </button>
              </div>
            </li>
          ))}
        </ul>
      ) : (
        <p>No ride requests found.</p>
      )}
    </div>
  );
};

export default TripsPage;
