import React, { useState } from 'react';
import '../styles/StartRideForm.css';
import { startRide } from '../services/rideService';

const StartRideForm = () => {
  const [formData, setFormData] = useState({
    originAddress: '',
    originCity: '',
    originZip: '',
    destinationAddress: '',
    destinationCity: '',
    destinationZip: '',
    availableSeats: 0,
    vehicleId: '',
    returnBack: false,
    returnTime: '',
    userId: 1, // Replace with logged-in user's ID
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    startRide(formData)
      .then(() => {
        alert('Ride started successfully!');
        setFormData({
          originAddress: '',
          originCity: '',
          originZip: '',
          destinationAddress: '',
          destinationCity: '',
          destinationZip: '',
          availableSeats: 0,
          vehicleId: '',
          returnBack: false,
          returnTime: '',
          userId: 1,
        });
      })
      .catch((error) => console.error('Error starting ride:', error));
  };

  return (
    <form className="start-ride-form" onSubmit={handleSubmit}>
      {/* Form fields go here */}
      <button type="submit" className="btn btn-success">Start Ride</button>
    </form>
  );
};

export default StartRideForm;
