import React, { useState } from 'react';
import '../styles/StartRideForm.css';
import { startRide } from '../services/rideService';

const StartRideForm = () => {
  const [formData, setFormData] = useState({
    originAddress: '',
    originCity: '',
    originZip: '',
    destinationAddress: '',
    destinationCity: '',
    destinationZip: '',
    availableSeats: 0,
    vehicleId: '',
    returnBack: false,
    returnTime: '',
    userId: 1, // Replace with logged-in user's ID
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    rideService(formData)
      .then(() => {
        alert('Ride started successfully!');
        setFormData({
          originAddress: '',
          originCity: '',
          originZip: '',
          destinationAddress: '',
          destinationCity: '',
          destinationZip: '',
          availableSeats: 0,
          vehicleId: '',
          returnBack: false,
          returnTime: '',
          userId: 1,
        });
      })
      .catch((error) => console.error('Error starting ride:', error));
  };

  return (
    <form className="start-ride-form" onSubmit={handleSubmit}>
      <input
        type="text"
        name="originAddress"
        placeholder="Origin Address"
        value={formData.originAddress}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="originCity"
        placeholder="Origin City"
        value={formData.originCity}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="originZip"
        placeholder="Origin Zip"
        value={formData.originZip}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="destinationAddress"
        placeholder="Destination Address"
        value={formData.destinationAddress}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="destinationCity"
        placeholder="Destination City"
        value={formData.destinationCity}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="destinationZip"
        placeholder="Destination Zip"
        value={formData.destinationZip}
        onChange={handleChange}
        required
      />
      <input
        type="number"
        name="availableSeats"
        placeholder="Available Seats"
        value={formData.availableSeats}
        onChange={handleChange}
        required
      />
      <input
        type="text"
        name="vehicleId"
        placeholder="Vehicle ID"
        value={formData.vehicleId}
        onChange={handleChange}
        required
      />
      <label>
        <input
          type="checkbox"
          name="returnBack"
          checked={formData.returnBack}
          onChange={handleChange}
        />
        Return Back?
      </label>
      {formData.returnBack && (
        <input
          type="datetime-local"
          name="returnTime"
          placeholder="Return Time"
          value={formData.returnTime}
          onChange={handleChange}
        />
      )}
      <button type="submit" className="btn btn-success">Start Ride</button>
    </form>
  );
};

export default StartRideForm;
