import React, { useState } from 'react';
import '../styles/SearchForm.css';
import { searchRides, sendRequest } from '../services/rideService';

const SearchForm = ({ onSearchResults, userId }) => {
  const [formData, setFormData] = useState({
    originCity: '',
    originState: '',
    originZip: '',
    destinationCity: '',
    destinationState: '',
    destinationZip: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const filteredParams = Object.fromEntries(
      Object.entries(formData).filter(([_, value]) => value.trim() !== '')
    );
    searchRides(filteredParams)
      .then((response) => onSearchResults(response.data))
      .catch((error) => console.error('Error searching rides:', error));
  };

  const handleRequest = (ride) => {
    const requestPayload = {
      hopUserId: userId, // Current user's ID
      hopSessionId: ride.id, // Ride session ID
      requestTime: new Date().toISOString(),
    };

    sendRequest(requestPayload)
      .then(() => alert('Request sent successfully!'))
      .catch((error) => {
        console.error('Error sending request:', error);
        alert('Failed to send request.');
      });
  };

  return (
    <form className="search-form" onSubmit={handleSubmit}>
      {/* Search Fields */}
      <input
        type="text"
        name="originCity"
        placeholder="Origin City"
        value={formData.originCity}
        onChange={handleChange}
      />
      <input
        type="text"
        name="originState"
        placeholder="Origin State"
        value={formData.originState}
        onChange={handleChange}
      />
      <input
        type="text"
        name="originZip"
        placeholder="Origin Zip"
        value={formData.originZip}
        onChange={handleChange}
      />
      <input
        type="text"
        name="destinationCity"
        placeholder="Destination City"
        value={formData.destinationCity}
        onChange={handleChange}
      />
      <input
        type="text"
        name="destinationState"
        placeholder="Destination State"
        value={formData.destinationState}
        onChange={handleChange}
      />
      <input
        type="text"
        name="destinationZip"
        placeholder="Destination Zip"
        value={formData.destinationZip}
        onChange={handleChange}
      />
      <button type="submit" className="btn btn-primary">Search</button>

      {/* Ride Results */}
      <div className="ride-results">
        {onSearchResults.length > 0 ? (
          onSearchResults.map((ride) => (
            <div key={ride.id} className="ride-card">
              <p><strong>Origin:</strong> {ride.originCity}</p>
              <p><strong>Destination:</strong> {ride.destinationCity}</p>
              <p><strong>Available Seats:</strong> {ride.availableSeats}</p>
              {ride.hopUser.id === userId ? (
                <p><strong>Your Ride</strong></p>
              ) : (
                <button
                  className="btn btn-success"
                  onClick={() => handleRequest(ride)}
                >
                  Request to Join
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No rides found.</p>
        )}
      </div>
    </form>
  );
};

export default SearchForm;
