import React, { useState } from 'react';
import { SearchForm } from '../components/SearchForm';
import { requestToJoinRide } from '../services/rideService';
import '../styles/HomePage.css';

const HomePage = () => {
  const [searchResults, setSearchResults] = useState([]);
  const loggedInUserId = localStorage.getItem('userId'); // User ID from local storage

  const handleRequest = (rideId) => {
    requestToJoinRide(loggedInUserId, rideId)
      .then(() => {
        alert('Request sent successfully!');
        setSearchResults((prevResults) =>
          prevResults.filter((ride) => ride.id !== rideId) // Optional: Update UI
        );
      })
      .catch((error) => console.error('Error requesting ride:', error));
  };

  return (
    <div className="homepage">
      <h1>Search for a Ride</h1>
      <SearchForm onSearchResults={setSearchResults} />
      <div className="ride-results">
        {searchResults.length > 0 ? (
          searchResults.map((ride) => (
            <div key={ride.id} className="ride-card">
              <p><strong>Origin:</strong> {ride.originCity}</p>
              <p><strong>Destination:</strong> {ride.destinationCity}</p>
              <p><strong>Available Seats:</strong> {ride.availableSeats}</p>
              {ride.userId === loggedInUserId ? (
                <p className="your-ride-label">Your Ride</p>
              ) : (
                <button
                  className="btn btn-success"
                  onClick={() => handleRequest(ride.id)}
                >
                  Request to Join
                </button>
              )}
            </div>
          ))
        ) : (
          <p>No rides found.</p>
        )}
      </div>
    </div>
  );
};

export default HomePage;
