import React, { useState } from 'react';
import '../styles/StartRideForm.css';
import { startRide } from '../services/rideService';

const StartRideForm = () => {
  const [formData, setFormData] = useState({
    originAddress: '',
    originCity: '',
    originZip: '',
    destinationAddress: '',
    destinationCity: '',
    destinationZip: '',
    availableSeats: 0,
    vehicleId: '',
    returnBack: false,
    returnTime: '',
    userId: localStorage.getItem('userId'), // Fetch the user ID from localStorage
  });

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value,
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    startRide(formData)
      .then(() => {
        alert('Ride started successfully!');
        setFormData({
          originAddress: '',
          originCity: '',
          originZip: '',
          destinationAddress: '',
          destinationCity: '',
          destinationZip: '',
          availableSeats: 0,
          vehicleId: '',
          returnBack: false,
          returnTime: '',
          userId: localStorage.getItem('userId'), // Reset user ID
        });
      })
      .catch((error) => {
        console.error('Error starting ride:', error);
        alert('Failed to start the ride.');
      });
  };

  return (
    <form className="start-ride-form" onSubmit={handleSubmit}>
      <h2>Start a Ride</h2>
      <label>
        Origin Address:
        <input
          type="text"
          name="originAddress"
          value={formData.originAddress}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Origin City:
        <input
          type="text"
          name="originCity"
          value={formData.originCity}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Origin Zip:
        <input
          type="text"
          name="originZip"
          value={formData.originZip}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Destination Address:
        <input
          type="text"
          name="destinationAddress"
          value={formData.destinationAddress}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Destination City:
        <input
          type="text"
          name="destinationCity"
          value={formData.destinationCity}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Destination Zip:
        <input
          type="text"
          name="destinationZip"
          value={formData.destinationZip}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Available Seats:
        <input
          type="number"
          name="availableSeats"
          value={formData.availableSeats}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Vehicle ID:
        <input
          type="text"
          name="vehicleId"
          value={formData.vehicleId}
          onChange={handleChange}
          required
        />
      </label>
      <label>
        Return Back?
        <input
          type="checkbox"
          name="returnBack"
          checked={formData.returnBack}
          onChange={handleChange}
        />
      </label>
      {formData.returnBack && (
        <label>
          Return Time:
          <input
            type="datetime-local"
            name="returnTime"
            value={formData.returnTime}
            onChange={handleChange}
          />
        </label>
      )}
      <button type="submit" className="btn btn-success">Start Ride</button>
    </form>
  );
};

export default StartRideForm;
