import React, { useState } from 'react';
import { searchSession } from '../services/sessionService';
import './SearchRide.css';

const SearchRide = () => {
  const [searchParams, setSearchParams] = useState({
    originCity: '',
    originState: '',
    originZip: '',
    destinationCity: '',
    destinationState: '',
    destinationZip: '',
  });

  const [rides, setRides] = useState([]);
  const [error, setError] = useState('');

  const handleChange = (e) => {
    setSearchParams({ ...searchParams, [e.target.name]: e.target.value });
  };

  const handleSearch = async () => {
    try {
      const response = await searchSession(searchParams);
      if (response.length === 0) {
        setError('No rides found for the given search parameters.');
      } else {
        setError('');
        setRides(response);
      }
    } catch (err) {
      setError('Error searching for rides.');
    }
  };

  return (
    <div className="search-ride">
      <h3>Search for a Ride</h3>
      <div className="form-group">
        <input
          type="text"
          name="originCity"
          placeholder="Origin City"
          value={searchParams.originCity}
          onChange={handleChange}
        />
        <input
          type="text"
          name="originState"
          placeholder="Origin State"
          value={searchParams.originState}
          onChange={handleChange}
        />
        <input
          type="text"
          name="originZip"
          placeholder="Origin Zip"
          value={searchParams.originZip}
          onChange={handleChange}
        />
        <input
          type="text"
          name="destinationCity"
          placeholder="Destination City"
          value={searchParams.destinationCity}
          onChange={handleChange}
        />
        <input
          type="text"
          name="destinationState"
          placeholder="Destination State"
          value={searchParams.destinationState}
          onChange={handleChange}
        />
        <input
          type="text"
          name="destinationZip"
          placeholder="Destination Zip"
          value={searchParams.destinationZip}
          onChange={handleChange}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {error && <p className="error">{error}</p>}
      {rides.length > 0 && (
        <ul className="ride-list">
          {rides.map((ride) => (
            <li key={ride.id}>
              {ride.originCity} â†’ {ride.destinationCity} | Seats: {ride.availableSeats}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default SearchRide;
